            labels.push(data[i].name)
            dataCount.push(data[i].review_count)
        }

        comp.setState({
            data: {
                labels: labels,
                datasets: [
                  {
                    label: 'Count',
                    data: dataCount,
                    backgroundColor: 'rgb(60, 179, 113)',
