import React, { Component } from "react";
import { Row, Col, Button} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faChartBar, faHome } from "@fortawesome/free-solid-svg-icons";
import Header from "./header";
import { Table } from "reactstrap"
import {Redirect} from 'react-router-dom'
import { Bar } from "react-chartjs-2";




class YelpReviewChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            movetoStations: false,
            stations: [],
            data: {
                labels: ['Jan', 'Feb', 'Mar'],
                datasets: [
                  {
                    label: 'Draft',
                    data: [100,300, 200],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            }
        }
    }

    moveToHomePage() {
        this.setState({
            moveToHome: true
        })
    }

    moveToListOfStations(key) {
        let comp = this

        var axios = require('axios');
        var data = JSON.stringify({
        "find": this.props.location.state.find,
        "where": this.props.location.state.where,
        "placeName": key.name
        });

        var config = {
        method: 'post',
        url: 'http://localhost:4000/stations/find',
        headers: { 
            'Content-Type': 'application/json'
        },
        data : data
        };

        axios(config)
        .then(function (response) {
        console.log(JSON.stringify(response.data));
        //{"stations_found":"Successfully Retrieved"}
        if(response.data.stations_found === "Successfully Retrieved") {
            var axios = require('axios');

            var config = {
            method: 'get',
            url: 'http://localhost:4000/stations',
            headers: { }
            };

            axios(config)
            .then(function (response) {
            console.log(JSON.stringify(response.data));
            comp.setState({
                    stations: response.data, 
                    movetoStations: true
                })
            })
            .catch(function (error) {
            console.log(error);
            });        
        }
        })
        .catch(function (error) {
        console.log(error);
        });
    }

    render() {
        return (
            <div>
                {this.state.moveToHome? <Redirect to = {{
                    pathname: "/list_of_places",
                    state: { places: [], find: '', where: ''}
                }}
                />: null}
                <Header />
                <div style = {{textAlign: "left"}}>
                    <Row>
                        <Col md = {10}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToHomePage.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>

                     <div>
                     <div style = {{padding: "20px"}}>
                         <h4>Yelp Reviews Chart</h4>

                    <Row style = {{marginLeft: "30px"}}>
                        <Col md = {12} xs = {12}>
                        <div style={{ maxWidth: "650px" }}>
        <Bar
          data = {this.state.data}
          // Height of graph
          height={400}
          options={{
            maintainAspectRatio: false,
            scales: {
              yAxes: [
                {
                    scaleLabel: {
                        display: true,
                        labelString: 'Yelp Reviews Count'
                      },
                    ticks: {
                        beginAtZero: true,
                    },
                },

              ],
              xAxes: [
                  {
                    barPercentage: 0.3
                  }
              ]
            },
            legend: {
              labels: {
                fontSize: 15,
              },
            },
          }}
        />
      </div>
                        </Col>
                    </Row>

                    </div>
                    </div>
                        <hr />
                    {/* </Form> */}
                </div >
            </div>

        );
    }
}

export default (YelpReviewChart);
