import React, { Component } from "react";
import { Row, Col, Button, Card} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faArrowUp, faBicycle, faHome } from "@fortawesome/free-solid-svg-icons";
import Header from "./header";
import { Table } from "reactstrap"
import {
    withScriptjs,
    withGoogleMap,
    GoogleMap,
    Marker,
    InfoWindow
} from 'react-google-maps';
import { compose, withProps } from 'recompose';
import {Redirect} from 'react-router-dom'



class ListofStations extends Component {
    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            moveToPlace: false,
            markerLatitude: 41.882607,
            markerLongitude: -87.643548,
            label: 'You are Here',
        }
    }

    moveToHomePage() {
        this.setState({
            moveToHome: true
        })
    }

    moveToPlacesPage() {
        this.setState({
            moveToPlace: true
        })
    }

    renderMarkers = (map, maps) => {

        let marker = new maps.Marker({
           position: { lat: parseFloat(this.state.markerLatitude), lng: parseFloat(this.state.markerLongitude) },
           map,
           title: this.state.label
        });
        return marker;
     };

    render() {
        const MapWithAMarkerClusterer = compose(
            withProps({
                googleMapURL: "https://maps.googleapis.com/maps/api/js?key=AIzaSyD7sSn5S_wx5HMwrbsGO4lpKAknoJqxfZA&v=3.exp&libraries=geometry,drawing,places",
                loadingElement: <div style={{ height: `100%` }} />,
                containerElement: <div style={{ height: `100%` }} />,
                mapElement: <div style={{ height: `100%` }} />,
            }),
            withScriptjs,
            withGoogleMap
        )(props =>
            <GoogleMap
                defaultZoom={13}
                defaultCenter={{ lat: 41.882607, lng:  -87.643548 }}
            >
                {this.props.location.state.stations.map(marker => {
                    //const onClick = props.onClick.bind(this, marker)
                    return (
                        <Marker
                            key={marker.id}
                            position={{ lat: marker.latitude, lng: marker.longitude }}
                        >
                            <InfoWindow>
                                <div>
                                    {marker.stationName}
                                </div>
                            </InfoWindow>
                        </Marker>
                    )
                })}
            </GoogleMap>
        );
        return (
            <div>
                {this.state.moveToHome? <Redirect to = "/find" />: null}
                {this.state.moveToPlace? <Redirect to = {{
                    pathname: "/list_of_places",
                    state: { places: this.props.location.state.places, find: this.props.location.state.find, where: this.props.location.state.where}
                }}
                />: null}
                <Header />
                <div style = {{textAlign: "left"}}>
                    {/* <Form> */}
                    <Row>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button color = "primary" onClick = {this.moveToHomePage.bind(this)}>Home <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faHome} /></span></Button>
                        </Col>
                        {/* <Col md = {1}></Col> */}
                        <Col md = {3} style = {{padding: "20px"}}>
                            <Button color = "primary" onClick = {this.moveToHomePage.bind(this)}>Divvy Stations Heat Map <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faBicycle} /></span></Button>
                        </Col>
                        <Col md = {3} style = {{padding: "20px"}}>
                            <Button color = "primary" onClick = {this.moveToHomePage.bind(this)}>Divvy Trips - Trends & Seasonality <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowUp} /></span></Button>
                        </Col>
                        <Col md = {2}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button color = "primary" onClick = {this.moveToPlacesPage.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>

                     <hr />
                     <div>
                        <Table bordered={false}>
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Station Name</th>
                                    <th>availableBikes</th>
                                    <th>availableDocks</th>
                                    <th>is_renting</th>
                                    <th>lastCommunicationTime</th>
                                    <th>Latitude</th>
                                    <th>longitude</th>
                                    <th>status</th>
                                    <th>totalDocks</th>
                                    <th></th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                            {this.props.location.state.stations.map((rowData, index) => (
                                <tr>
                                <td>{rowData.id}</td>
                                <td>{rowData.stationName}</td>
                                <td>{rowData.availableBikes}</td>
                                <td>{rowData.availableDocks}</td>
                                <td>{String(rowData.is_renting)}</td>
                                <td>{rowData.lastCommunicationTime}</td>
                                <td>{rowData.latitude}</td>
                                <td>{rowData.longitude}</td>
                                <td>{rowData.status}</td>
                                <td>{rowData.totalDocks}</td>
                                <td><Button color = "primary" size="sm" onClick = {this.moveToHomePage.bind(this)}>Real-Time Chart <span style = {{marginLeft: "5px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button></td>
                                <td> <Button color = "primary" size="sm" onClick = {this.moveToHomePage.bind(this)}>SMA Chart <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button></td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>
                    </div>

                    <Card className="w-full h-512 rounded-8 shadow-1">
                        <div style={{height: `400px`, width: '100%'}}>
                            <MapWithAMarkerClusterer />
                        </div>
                     </Card>
                        <hr />
                    {/* </Form> */}
                </div >
            </div>

        );
    }
}

export default (ListofStations);
