import React, { Component } from "react";
import { Row, Col, Button} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faChartBar, faHome } from "@fortawesome/free-solid-svg-icons";
import Header from "./header";
import { Table } from "reactstrap"
import {Redirect} from 'react-router-dom'
import { Bar } from "react-chartjs-2";




class YelpReviewChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            movetoStations: false,
            stations: []
        }
    }

    moveToHomePage() {
        this.setState({
            moveToHome: true
        })
    }

    moveToListOfStations(key) {
        let comp = this

        var axios = require('axios');
        var data = JSON.stringify({
        "find": this.props.location.state.find,
        "where": this.props.location.state.where,
        "placeName": key.name
        });

        var config = {
        method: 'post',
        url: 'http://localhost:4000/stations/find',
        headers: { 
            'Content-Type': 'application/json'
        },
        data : data
        };

        axios(config)
        .then(function (response) {
        console.log(JSON.stringify(response.data));
        //{"stations_found":"Successfully Retrieved"}
        if(response.data.stations_found === "Successfully Retrieved") {
            var axios = require('axios');

            var config = {
            method: 'get',
            url: 'http://localhost:4000/stations',
            headers: { }
            };

            axios(config)
            .then(function (response) {
            console.log(JSON.stringify(response.data));
            comp.setState({
                    stations: response.data, 
                    movetoStations: true
                })
            })
            .catch(function (error) {
            console.log(error);
            });        
        }
        })
        .catch(function (error) {
        console.log(error);
        });
    }

    render() {
        return (
            <div>
                {this.state.moveToHome? <Redirect to = "/find" />: null}
                {this.state.movetoStations? <Redirect to = {{
                    pathname: "/list_of_stations",
                    state: { stations: this.state.stations, places: this.props.location.state.places, find: this.props.location.state.find, where: this.props.location.state.where}
                }} 
                /> :null }
                <Header />
                <div style = {{textAlign: "left"}}>
                    <Row>
                        <Col md = {10}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToHomePage.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>

                     <div>
                     <div style = {{padding: "20px"}}>
                         <h4>Yelp Review Chart</h4>

                    <Row style = {{marginLeft: "30px"}}>
                        <Col md = {12} xs = {12}>
                        <div style={{ maxWidth: "650px" }}>
        <Bar
          data={{
            // Name of the variables on x-axies for each bar
            labels: ["1st bar", "2nd bar", "3rd bar", "4th bar"],
            datasets: [
              {
                // Label for bars
                label: "total count/value",
                // Data or value of your each variable
                data: [1552, 1319, 613, 1400],
                // Color of each bar
                backgroundColor: ["aqua", "green", "red", "yellow"],
                // Border color of each bar
                borderColor: ["aqua", "green", "red", "yellow"],
                borderWidth: 0.5,
              },
            ],
          }}
          // Height of graph
          height={400}
        //   options={{
        //     maintainAspectRatio: false,
        //     // scales: {
        //     //   yAxes: [
        //     //     {
        //     //       ticks: {
        //     //         // The y-axis value will start from zero
        //     //         beginAtZero: true,
        //     //       },
        //     //     },
        //     //   ],
        //     // },
        //     legend: {
        //       labels: {
        //         fontSize: 15,
        //       },
        //     },
        //   }}
        />
      </div>
                        </Col>
                    </Row>

                    </div>
                    </div>
                        <hr />
                    {/* </Form> */}
                </div >
            </div>

        );
    }
}

export default (YelpReviewChart);
