import React, { Component } from "react";
import { Row, Col, Button, Input, Card} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faChartBar, faHome } from "@fortawesome/free-solid-svg-icons";
import Header from "./header";
import {Redirect} from 'react-router-dom'
import { Map, GoogleApiWrapper, HeatMap } from "google-maps-react";

// const data = [
//     { lat: 37.782551, lng: -122.44536800000003 },
//     { lat: 37.782745, lng: -122.44458600000002 },
//     { lat: 37.782842, lng: -122.44368800000001 },
//     { lat: 37.782919, lng: -122.442815 },
//     { lat: 37.782992, lng: -122.44211200000001 },
//     { lat: 37.7831, lng: -122.441461 },
//     { lat: 37.783206, lng: -122.44082900000001 },
//     { lat: 37.783273, lng: -122.44032400000003 },
//     { lat: 37.783316, lng: -122.440023 },
//     { lat: 37.783357, lng: -122.439794 },
//     { lat: 37.783371, lng: -122.43968699999999 },
//     { lat: 37.783368, lng: -122.43966599999999 },
//     { lat: 37.783383, lng: -122.439594 },
//     { lat: 37.783508, lng: -122.439525 },
//     { lat: 37.783842, lng: -122.43959100000001 },
//     { lat: 37.784147, lng: -122.43966799999998 },
//     { lat: 37.784206, lng: -122.439686 },
//     { lat: 37.784386, lng: -122.43979000000002 }
//   ];

class HeatMapChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            movetoStations: false,
            stations: [],
            data: [],   
            gradient: [],  
            center: {
                lat: 41.882607, 
                lng:  -87.643548
            }
        }
    }

    componentDidMount() {
        let comp = this
        var axios = require('axios');
        var config = {
        method: 'get',
        url: 'http://localhost:3000/heatmap',
        headers: { }
        };

        axios(config)
        .then(function (response) {
        // console.log(JSON.stringify(response.data));
        let gradient = []
        let heatmapData = []
                for(let i=0; i< response.data[0].length; i++) {
                    if(response.data[0][i].ccvi > 50) {
                        gradient.push( "rgba(0, 255, 255, 0)")
                    } else {
                        gradient.push("rgba(0, 63, 255, 1)")
                    }
                    const heatMapDict = {
                        lat: response.data[0][i].latitude,
                        lng: response.data[0][i].longitude
                    }
                    heatmapData.push(heatMapDict)
                }

                comp.setState({
                    data: heatmapData,
                    gradient: gradient
                })
        })
        .catch(function (error) {
        console.log(error);
        });

    }

    moveToListOfStations() {
        this.setState({
            movetoStations: true
        })
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        // const gradient = [
        //     "rgba(0, 255, 255, 0)",
        //     "rgba(0, 255, 255, 1)",
        //     "rgba(0, 191, 255, 1)",
        //     "rgba(0, 127, 255, 1)",
        //     "rgba(0, 63, 255, 1)",
        //     "rgba(0, 0, 255, 1)",
        //     "rgba(0, 0, 223, 1)",
        //     "rgba(0, 0, 191, 1)",
        //     "rgba(0, 0, 159, 1)",
        //     "rgba(0, 0, 127, 1)",
        //     "rgba(63, 0, 91, 1)",
        //     "rgba(127, 0, 63, 1)",
        //     "rgba(191, 0, 31, 1)",
        //     "rgba(255, 0, 0, 1)"
        //   ];
        return (
            <div>
                {this.state.movetoStations? <Redirect to = {{
                    pathname: "/list_of_stations",
                    state: { stations: [], places: [], find: '', where: ''}
                }} 
                /> :null }
                <Header />
                <div style = {{textAlign: "left"}}>
                    <Row>
                        <Col md = {10}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToListOfStations.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>

                    <Row>
                        <Col md = {2} style = {{padding: "20px"}}> Time Range
                            <Input type = "select" style = {{border: "none", borderBottom: "1px solid black"}} name="find" onChange = {this.handleChange.bind(this)}  placeholder = "Find">
                                <option value = "PAST HOUR">Past Hour</option>
                                <option value = "LAST 24 HOURS">Last 24 Hours</option>
                                <option value = "LAST 7 DAYS">Last 7 Days</option>
                            </Input>
                        </Col>
                    </Row>

                    {console.log("Heat map data", this.state.data)}
                    {console.log("Gradient", this.state.gradient)}

                     <div style = {{padding: "20px"}}>
                     <Card className="w-full h-512 rounded-8 shadow-1">
                        <div style={{height: `400px`, width: '100%'}}>
                        <Map
                            google={this.props.google}
                            className={"map"}
                            zoom={8}
                            initialCenter={this.state.center}
                            onReady={this.handleMapReady}
                            >
                            <HeatMap
                                gradient={this.state.gradient}
                                positions={this.state.data}
                                opacity={1}
                                radius={20}
                            />
                            </Map>
                        </div>
                     </Card>

                    </div>
                    </div>
                </div >

        );
    }
}

export default GoogleApiWrapper({
    apiKey: "AIzaSyD7sSn5S_wx5HMwrbsGO4lpKAknoJqxfZA",
    libraries: ["visualization"]
  })(HeatMapChart);
