import React, { Component } from "react";
import { Row, Col, Button, Input} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft, faChartBar, faHome } from "@fortawesome/free-solid-svg-icons";
import Header from "./header";
import { Table } from "reactstrap"
import {Redirect} from 'react-router-dom'
import { Line } from "react-chartjs-2";




class RealTimeChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            movetoStations: false,
            stations: [],
            data: {
                labels: [],
                datasets: [
                  {
                    label: 'Real-Time Data',
                    fill: false,
                    lineTension: 0.1,
                    data: [],
                    backgroundColor: 'rgb(255, 165, 0)',
                    borderColor: 'rgba(255, 165, 0)',
                  },
                ],
            }
        }
    }

    moveToHomePage() {
        this.setState({
            moveToHome: true
        })
    }

    moveToListOfStations(key) {
        let comp = this

        var axios = require('axios');
        var data = JSON.stringify({
        "find": this.props.location.state.find,
        "where": this.props.location.state.where,
        "placeName": key.name
        });

        var config = {
        method: 'post',
        url: 'http://localhost:4000/stations/find',
        headers: { 
            'Content-Type': 'application/json'
        },
        data : data
        };

        axios(config)
        .then(function (response) {
        console.log(JSON.stringify(response.data));
        //{"stations_found":"Successfully Retrieved"}
        if(response.data.stations_found === "Successfully Retrieved") {
            var axios = require('axios');

            var config = {
            method: 'get',
            url: 'http://localhost:4000/stations',
            headers: { }
            };

            axios(config)
            .then(function (response) {
            console.log(JSON.stringify(response.data));
            comp.setState({
                    stations: response.data, 
                    movetoStations: true
                })
            })
            .catch(function (error) {
            console.log(error);
            });        
        }
        })
        .catch(function (error) {
        console.log(error);
        });
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div>
                {this.state.moveToHome? <Redirect to = {{
                    pathname: "/list_of_places",
                    state: { places: [], find: '', where: ''}
                }}
                />: null}
                <Header />
                <div style = {{textAlign: "left"}}>
                    <Row>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToHomePage.bind(this)}>Home<span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                        <Col md = {8}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToHomePage.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>

                    <Row>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Input type = "select" style = {{border: "none", borderBottom: "1px solid black"}} name="find" onChange = {this.handleChange.bind(this)}  placeholder = "Find">
                                <option value = "PAST HOUR">Past Hour</option>
                                <option value = "LAST 24 HOURS">Last 24 Hours</option>
                                <option value = "LAST 7 DAYS">Last 7 Days</option>
                            </Input>
                        </Col>
                    </Row>

                     <div>
                     <div style = {{padding: "20px"}}>
                         <h4>Divvy Dock Station: </h4>

                    <Row>
                        <Col md = {8} xs = {12}>
                        <div>
                            <Line
                            data = {this.state.data}
                            // Height of graph
                            height={600}
                            options={{
                                maintainAspectRatio: false,
                                scales: {
                                yAxes: [
                                    {
                                        scaleLabel: {
                                            display: true,
                                            labelString: 'Available Docks'
                                        },
                                        ticks: {
                                            beginAtZero: true,
                                        },
                                    },

                                ],
                                xAxes: [
                                    {
                                        scaleLabel: {
                                            display: true,
                                            labelString: 'Time'
                                        },
                                    }
                                ]
                                },
                                legend: {
                                labels: {
                                    fontSize: 15,
                                },
                                },
                            }}
                            />
                        </div>
                        </Col>
                    </Row>

                    </div>
                    </div>
                </div >
            </div>

        );
    }
}

export default (RealTimeChart);
