    constructor(props) {
        super(props);
        this.state = {
            moveToHome: false,
            movetoStations: false,
            stations: [],
            data1: {
                labels: [],
                datasets: [
                  {
                    label: 'Monday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data2: {
                labels: [],
                datasets: [
                  {
                    label: 'Tuesday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data3: {
                labels: [],
                datasets: [
                  {
                    label: 'Wednesday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data4: {
                labels: [],
                datasets: [
                  {
                    label: 'Thursday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data5: {
                labels: [],
                datasets: [
                  {
                    label: 'Friday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data6: {
                labels: [],
                datasets: [
                  {
                    label: 'Saturday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            data7: {
                labels: [],
                datasets: [
                  {
                    label: 'Sunday',
                    data: [],
                    backgroundColor: 'rgb(60, 179, 113)',
                  }
                ],
            },
            options: {
                maintainAspectRatio: false,
                scales: {
                yAxes: [
                    {
                        scaleLabel: {
                            display: true,
                            labelString: 'Divvy Trips'
                        },
                        ticks: {
                            beginAtZero: true,
                        },
                    },

                ],
                xAxes: [
                    {
                        scaleLabel: {
                            display: true,
                            labelString: 'Time'
                        },
                        barPercentage: 0.1
                    }
                ]
                },
                legend: {
                labels: {
                    fontSize: 15,
                },
                },
            }
        }
    }
    componentDidMount(){
        var axios = require('axios');
        var data = JSON.stringify({
        "day": 2,
        "selectedDate": "2018-10-02T00:00:00.000Z"
        });

        var config = {
        method: 'post',
        url: 'http://localhost:4000/countDivvyTripsPerDay',
        headers: { 
            'Content-Type': 'application/json'
        },
        data : data
        };

        axios(config)
        .then(function (response) {
        console.log(JSON.stringify(response.data));
        if(response.data.TUESDAY === "Successfully Retrieved") {
                var axios = require('axios');
                let data2label = []
                let data2Value = []
                var config = {
                method: 'get',
                url: 'http://localhost:4000/getDivvyTripsCountsForTuesday',
                headers: { }
                };

                axios(config)
                .then(function (response) {
                console.log(JSON.stringify(response.data));
                for (let i = 0; i<response.data.length; i++){
                    data2label.push(response.data[i].hour_timestamp)
                    console.log(data2label)
                }
                })
                .catch(function (error) {
                console.log(error);
                });


        }
        })
        .catch(function (error) {
        console.log(error);
        });



    }

    moveToHomePage() {
        this.setState({
            moveToHome: true
        })
    }

    moveToListOfStations() {
        this.setState({
            movetoStations: true
        })
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div>
                {this.state.moveToHome? <Redirect to = "/find" />: null}
                {this.state.movetoStations? <Redirect to = {{
                    pathname: "/list_of_stations",
                    state: { stations: [], places: [], find: '', where: ''}
                }} 
                /> :null }
                <Header />
                <div style = {{textAlign: "left"}}>
                    <Row>
                        <Col md = {10}></Col>
                        <Col md = {2} style = {{padding: "20px"}}>
                            <Button style = {{backgroundColor: "#191970"}} onClick = {this.moveToListOfStations.bind(this)}>Go Back <span style = {{marginLeft: "10px"}}><FontAwesomeIcon icon = {faArrowLeft} /></span></Button>
                        </Col>
                    </Row>
{/* 
                    <Row>
                        <Col md = {2} style = {{padding: "20px"}}> Choose a Week
                            <Input type = "date" style = {{border: "none", borderBottom: "1px solid black"}} name="find" onChange = {this.handleChange.bind(this)}  placeholder = "Find">
                                <option value = "10/8/2018-10/14/2018">10/8/2018-10/14/2018</option>
                            </Input>
                        </Col>
                    </Row> */}

                     <div>
                     <div style = {{padding: "20px"}}>
                    <Row>
                        <Col md = {2}></Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data1}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data2}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col md = {2}></Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data3}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data4}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col md = {2}></Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data5}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data6}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col md = {2}></Col>
                        <Col md = {4} xs = {4}>
                        <Bar
                            data = {this.state.data7}
                            height={300}
                            options={this.state.options}
                            />
                        </Col>
                    </Row>

                    </div>
                    </div>
                </div >
            </div>

        );
    }
}

